INSERT INTO `t_blogger` VALUES ('1', 'admin', 'c5c6fd2a2b5fbfd91c1a24d2c5ff1937', '<p>网站功能介绍</p><ol class=\" list-paddingleft-2\" style=\"list-style-type: decimal;\"><li><p><span style=\"color: rgb(0, 0, 0);\">使用Maven3+Spring4+Springmvc+Mybatis3架构；数据库使用Mysql，数据库连接池使用阿里巴巴的Druid；</span></p></li><li><p><span style=\"color: rgb(0, 0, 0);\">使用Bootstrap3 UI框架实现博客的分页显示，博客分类，文章归类显示；完成用户评论和分享功能；</span></p></li><li><p><span style=\"color: rgb(0, 0, 0);\">使用EasyUI实现后台对博客、博客类别、用户评论、博主信息的管理，包括增删改查，文件上传等；实现刷新后台缓存等功能；</span></p></li><li><p><span style=\"color: rgb(0, 0, 0);\">使用Shiro作为项目安全框架，验证不同url的请求，包括后台博主的登陆；</span></p></li><li><p><span style=\"color: rgb(0, 0, 0);\">实现Lucene对全站的检索功能，对检索出的博客标题和内容实现高亮显示；</span></p></li><li><p><span style=\"color: rgb(0, 0, 0);\">使用百度的Ueditor编辑器实现写博客功能，支持单图、多图上传，支持截图上传，支持代码高亮特性等。</span></p></li></ol><p><br/></p><p><span style=\"color: rgb(0, 0, 0);\">QQ：499868157</span></p><p><span style=\"color: rgb(0, 0, 0);\">Mail：499868157@qq.com</span></p>', 'star', 'Always be Lucky!', '20170504035500.jpg');

